import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { loadingState } from '../actions';

const GITHUB_SEARCH = `https://api.github.com/users/`;

class UserProfile extends Component {
    constructor(props) {
        super(props) 
        this.state = {
            userData: [],
            repos: []
        }
    }

    componentDidMount() {
        this.props.loadingState(true)
        axios
        .get(`${GITHUB_SEARCH}${this.props.match.params.login}`)
        .then(response => this.setState({
            userData: response.data
        }))
        .then(this.props.loadingState(false))
        .catch(error => this.setState({
            error,
          }))     
    }
    componentWillMount() {
        this.props.loadingState(true)
        axios
          .get(`${GITHUB_SEARCH}${this.props.match.params.login}/repos`)
          .then(response => this.setState({
              repos: response.data,
          }))
          .then(this.props.loadingState(false))
          .catch(error => this.setState({
              error,
            }))
    }

    render() {
        const { userData, repos }  = this.state
        console.log(this.state.userData)
        console.log(this.state.repos)

        if (!this.props.loading) {
            return <div>Loading</div>
        }
        return (
            <div>
                <img src={userData.avatar_url} alt={userData.login} />
                <p>{userData.login}</p>
                <p>{userData.public_repos}</p>
                {repos.map(repo => <li key={repo.id}>{repo.id}</li>)}
            </div>
        )
    }
}

const mapStateToProps = state => {
    return { loading: state.loadingState }
}

export default connect(mapStateToProps, { loadingState })(UserProfile)
