import { FETCH_USERS } from '../actions';

const initialState = {
    isLoading: false,
    users: []
}

export default function(state = initialState, action) {
    switch (action.type) {
        case FETCH_USERS:
            return {
                isLoading: true,
                users: action.payload
            }
        default:
            return state;
    }
}
