import React, { Component } from 'react'
import { connect } from 'react-redux';

import { UsersContiner } from '../resources/styles/styled-Github_Users'

class GithubUsers extends Component {

    handleClick = (e) => {
        console.log(e.target.value)
    }

    render() {
        if (this.props.user.isLoading) {
            return <div>Loading</div>
        }
        return (
            <div>
                <ul>
                    {this.props.user.users.map(user => 
                        <li key={user.id}>
                            <img 
                            style={{width: '200px', height: '200px'}} 
                            src={user.avatar_url} 
                            alt={user.login} />
                            <p>{user.login}</p>
                            <p>{user.id}</p>
                            <button value={user.id} onClick={this.handleClick}>View</button>
                        </li>
                    )}
                </ul>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return { user: state.addUser }
}

export default connect(mapStateToProps)(GithubUsers);
