import React from 'react'

import '../resources/styles/styled-search.sass';

const Search = ( { handleSubmit, handleChange, inputVal, plus, minus, users, page} ) => {
    let plusBtn, minusBtn, pageCount;

    if (users.length > 0) {
        plusBtn = <button onClick={plus}>Next</button>;
        pageCount = <p>{page.pageNr}</p>

        if (page.pageNr > 0) { 
          minusBtn = <button onClick={minus}>Prev</button>;
        }
      }

    return (
        <form className='searchForm' onSubmit={handleSubmit} >
            <input 
            type='text' 
            placeholder='Type username'
            ref={inputVal} 
            onChange={handleChange} />
            <button type='submit'>Search</button>
            <div className='pagesNavigation'>
            {minusBtn}
            {pageCount}
            {plusBtn}
        </div>
    </form>
    )
}

export default Search
