import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchUsers } from './actions';

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      local: ''
    }
    this.inputVal = React.createRef();
  }

  componentDidMount() {
    this.props.fetchUsers(this.state.local);
  }

  handleSubmit = e => {
    e.preventDefault();
    this.setState( { local: e.inputVal.current.value})
  }

  render() {
    console.log(this.props.addUser)
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit} >
                <input type='text' ref={this.inputVal} />
                <button type='submit'>Search</button>
          </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { addUser: state.addUser }
}

export default connect(mapStateToProps, { fetchUsers })(App);
