import { PAGE_PLUS, PAGE_MINUS } from '../actions';

const increment = (state = 1, action) => {
    switch (action.type) {
        case PAGE_PLUS:
            return {
                ...state,
                value: state.value + 1
            }
        case PAGE_MINUS:
            return {
                ...state,
                value: state.value - 1
            }
        default: 
            return state
    }
}

export default increment;
