import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchUsers } from './actions';

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      local: []
    }
    this.inputVal = React.createRef();
  }

  componentDidMount() {
    this.props.fetchUsers(this.state.local);
  }

  addItem = (e) => {
    if (this.inputTask.current.value !== "") {
      var newItem = {
        text: this.inputTask.current.value,
        key: Date.now()
      };

      this.setState((prevState) => {
        return { 
          tasks: prevState.tasks.concat(newItem) 
        };
      });

      this.inputTask.current.value = "";
    }
    e.preventDefault();
  }


  render() {
    console.log(this.props.addUser)
    return (
      <div className="App">
        <form onSubmit={this.addItem}>
              <label>Name</label>
              <input type="text" 
              ref={this.inputTask}
              />
              <button type='submit'>add</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { addUser: state.addUser }
}

export default connect(mapStateToProps, { fetchUsers })(App);
