import React, { Component } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import { fetchUsers } from './actions';

import './App.css';


const GITHUB_SEARCH = `https://api.github.com/search/users?q=`;
const SEARCH_LIMIT = '&per_page=6';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      local: [],
      users: []
    }
    this.inputVal = React.createRef();
  }

  componentDidMount() {
    this.props.fetchUsers(this.state.local);
  }

  handleSubmit = e => {
    this.setState({ local: this.inputVal.current.value })
    axios
    .get(`${GITHUB_SEARCH}${this.state.local}${SEARCH_LIMIT}`)
    .then(result => {
      this.setState( { 
        users: result.data.items,
        isLoading: false
      }), console.log(result.data.items[2])
    })
    .catch(error => this.setState({
      error,
    }))
    e.preventDefault();
  }

  render() {
    console.log(this.props.addUser)
    console.log(this.state.local)
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit} >
                <input type='text' ref={this.inputVal} />
                <button type='submit'>Search</button>
          </form>
        <button onClick={e => this.props.fetchUsers(this.state.local)}>Fetch</button>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { addUser: state.addUser }
}

export default connect(mapStateToProps, { fetchUsers })(App);
